classes.content:
self {
	border-radius: 4px;
	border: 1px solid var(--accent-color1);
	color: var(--accent-color1);
	background-color:var(--background-color-button);
	padding: 10px;
	font-weight: 600;
}

self {
  transition-duration: 0.4s;
}

self:hover {
  border: 1px solid var(--accent-color1);
  background-color: var(--accent-color1);
  color: #FFFFFF;
}

self {
    background-color: var(--accent-color1);
    color: #FFFFFF;

    border-radius: 10px 0 0 10px;
    width: 50%;
    height: 100%;
}

@media screen and (max-width: 800px) {
    self {
      width: 100%;
      height: fit-content;
      padding-top: 40px;
      padding-bottom: 40px;

      border-radius: 10px 10px 0 0;
    }
	}

self {
   color: var(--text-color-label);
   background-color: transparent;
}

self.disabled {
	opacity: 1;
}

@media (prefers-color-scheme: dark) {
	self {
   		color: var(--text-color-label);
   		background-color: transparent;
	}

	self.disabled {
		opacity: 0.5;
	}
}

self {
	border: 1px solid #ced4da;
	padding-right : 6px;
	padding-bottom : 6px;
	padding-top : 6px;
	padding-left : 6px;
	color: #000000;
}

self.disabled {
	opacity: 0.9 !important;
	color: #000000;
}

@media (prefers-color-scheme: dark) {
	self {
		border: 1px solid #717275 !important; 
		padding-right: 6px;
		padding-bottom: 6px;
		padding-top: 6px;
		padding-left: 6px;
		background-color: #0C0E12;
		color: #c0c0c0;
	}

	self.disabled {
		opacity: 0.9 !important;
		color: #c0c0c0 !important;
	}
}

self {
   font-size: xx-large;
}

self {
  border-collapse: collapse;
}

self .header{
    height: fit-content !important;
    border-width: 0px;
}

self .header .header-cell{
    font-size: 18px;
    background-color: #4f5963;
    color:  #FFFFFF;
    border-color: #aaadaf;
    border-width: 0 0.5px 0 0.5px;
}

self .header .header-cell .headerTruncatedText{
    white-space: normal;
}

self .row.selected {
	color: var(--selected-color);
	background-color: var(--selected-background-color);
}

self .row-even:not(.selected):not(:hover) {
  background-color: #f8f8f8;
}

@media (prefers-color-scheme: dark) {
  self {
        border-collapse: collapse;
        color: #e0e0e0;
    }

    self .header {
        height: fit-content !important;
        border-width: 0px;
    }

    self .header .header-cell {
        font-size: 18px;
        background-color: #212121;
        color: #e0e0e0;
        border-color: #444;
        border-width: 0 0.5px 0 0.5px;
    }

    self .header .header-cell .headerTruncatedText {
        white-space: normal;
    }

    self .row.selected {
		color: var(--selected-color);
		background-color: var(--selected-background-color);
    }

    self .row-even:not(.selected):not(:hover) {
        background-color: #2C2C2C; 
    }
}

self {
  box-shadow: rgba(0, 0, 0, 0.1) 0px 1px 4px 0px;
}

@media (prefers-color-scheme: dark) {
	self {
    box-shadow: rgba(255, 255, 255, 0.1) 0px 1px 4px 0px;
  }
}

self {
	border-radius: 4px;
	border: 1px solid #4f5963;
	color: #4f5963;
	background-color: #FFFFFF;
	padding: 10px;
	font-weight: 600;
	transition-duration: 0.4s;
}

self:hover {
  border: 1px solid #394047;
  background-color: #394047;
  color: #FFFFFF;
}

@media (prefers-color-scheme: dark) {
	self {
		border-radius: 4px;
		border: 1px solid #e0e0e0; 
		color: #e0e0e0; 
		background-color: #1e1e1e; 
		padding: 10px;
		font-weight: 600;
		transition-duration: 0.4s;
	}

	/* Hover */
	self:hover {
		border: 1px solid #e0e0e0;
		background-color: #e0e0e0;
		color: #1e1e1e; 

	}
}

self {
   background-color: #f3bec3;
}

@media (prefers-color-scheme: dark) {
	self {
      background-color: #c32232; /* Fond sombre adaptÃ© */
    }
}

self {
	color: var(--text-color-menubar-selected);
	font-size: 18px;
}

self:hover {
  color: var(--text-color-menubar-selected-hover);
}

self {
	color: var(--text-color-menubar);
	font-size: 18px;
	background-color: transparent !important; 
}

self:hover {
	color: var(--text-color-menubar-hover);
	font-size: 18px;
}

self {
	border-radius: 4px;
	border: 1px solid var(--accent-color1);
	background-color: var(--accent-color1);
	color: #FFFFFF;
}

self {
  transition-duration: 0.4s;
}

self:hover {
  border: 1px solid var(--accent-color2);
  background-color: var(--accent-color2);
  color: #FFFFFF;
}

self {
	min-width: 200px;
	max-width: 400px;
}

self .fd-selectbox__toggle_button {
	border: 1px solid #ced4da;
	border-radius: 4px;
}

self.fd-selectbox__menu {
	border: 0px;
	background-color: white;
}

/*Selected item */
self .selected > .fd-selectbox__container {
	color: var(--selected-color);
	background-color: var(--selected-background-color);
    opacity: 1;
}


self .disabled {
	opacity: 0.90;
}

@media (prefers-color-scheme: dark) {
    self {
        min-width: 200px;
        max-width: 400px;
    }

	self .fd-selectbox__toggle_button {
		background-color: #0C0E12;
        color: #c0c0c0;
		border: 1px solid #717275 !important; 
		border-radius: 4px;
	}

	self.fd-selectbox__menu {
		border: 0px;
		background-color: #0C0E12;
		color: #c0c0c0;
	}

	self .selected > .fd-selectbox__container {
	color: var(--selected-color);
	background-color: var(--selected-background-color);
    	opacity: 1;
	}

    self .disabled {
    	opacity: 0.9 !important;
		color: #c0c0c0 !important;
    }
}

self button[data-headlessui-state] {
	color: var(--text-color-label);
	background-color: transparent;
	border-color: transparent !important;
	font-size: 16px !important;
	font-weight: 400;
	padding: 10px 10px;
}

self button[data-headlessui-state="selected"]:after {
	background-color: var(--accent-color1) ;
}

self button[data-headlessui-state]:hover {
	color: var(--text-color-label-hover);
  }

self button[data-headlessui-state="selected"] {
	color: var(--accent-color1) !important;
}

self {
  padding-right : 0px;
  padding-bottom : 0px;
  padding-top : 0px;
  padding-left : 0px;
}

self {
  height : 100VH-108px;
}

self {
  max-width : 200px;
  border-radius : 4px;
}

self {
  box-shadow: 5px 10px 18px #888888; 
	border-radius: 10px;
  }

  @media (prefers-color-scheme: dark) {
    self {
      box-shadow: 5px 10px 18px #1e1e1e;
			border-radius: 10px;
    }
	}

self {
	background-color: var(--main-bg-color);
	color: var(--main-fg-color);
}

self {
  background-color : var(--sidebar-bg-color);
}

self {
  height : 250px;
  width : 300px;
  border-radius : 4px;
  padding : 8px;
  background-color : white;
}

@media (prefers-color-scheme: dark) {
	self {
    height : 250px;
    width : 300px;
    border-radius : 4px;
    padding : 8px;
    background-color : #0c0c0c;
	}
}

self {
	background-color: var(--footer-bg-color);
	color: var(--footer-fg-color);
	text-align: center;
}

self {
  height : 250px;
  width : 300px;
  border-radius : 4px;
  padding : 8px;
  background-color : white;
}

self .datePicker-title, .yearPicker-title, .datePicker-leftIcon, .datePicker-rightIcon, .yearPicker-leftIcon, .yearPicker-rightIcon  {
	color: #000000;
	font-weight: 400;
}

self .datePicker-days, .datePicker-weekDay, .yearPicker-years {
	color: #717275;
	font-weight: 400;
}

self .yearPicker-selectedYear, .datePicker-selectedDay {
	color: var(--selected-color);
	font-weight: normal;
	/*background-color: var(--selected-background-color) !important;*/
}

th[style*="background-color: rgb(34, 139, 230)"], td[style*="background-color: rgb(34, 139, 230)"] {
	background-color: var(--selected-background-color) !important;
}

@media (prefers-color-scheme: dark) {
	self {
		height : 250px;
		width : 300px;
		border-radius : 4px;
		padding : 8px;
		background-color : #121212;
	  }

	self .datePicker-title, .yearPicker-title, .datePicker-icon, .yearPicker-leftIcon, .yearPicker-rightIcon  {
		color: #FFFFFF;
		font-weight: 400;
	}

	self .datePicker-days, .datePicker-weekDay, .yearPicker-years {
		color: #e0e0e0;
		font-weight: 400;
	  }

	self .yearPicker-selectedYear, .datePicker-selectedDay {
		color: var(--selected-color);
		font-weight: 400;
		/*background-color: var(--selected-background-color) !important;*/
	  }

	th[style*="background-color: rgb(34, 139, 230)"], td[style*="background-color: rgb(34, 139, 230)"] {
		background-color: var(--selected-background-color) !important;
	}
}

:root {
	--main-bg-color: #FFFFFF;
	--main-fg-color: #000000;
	--sidebar-bg-color: #f5f5f5;
	--footer-bg-color: #f0f0f0;
	--footer-fg-color: #3c3c3c;

	--accent-color1: #dd3c4c; 
	--accent-color2: #c32232;
	--accent-color3: #f3bec3;

	--text-color1: #000000;
	--text-color2: #000000;
	--text-color3: #717275;

	--background-color-button: #FFFFFF; 

	/*
#4f5963 button
#394047 button hover

#4f5963 datatable header
#aaadaf datatable header border
#f8f8f8 datatable alternate
	*/

	/* Menu */
	--text-color-menubar: #4f5963;
	--text-color-menubar-hover: #394047;
	--text-color-menubar-selected: var(--accent-color1);
	--text-color-menubar-selected-hover: var(--accent-color2);

	/* Label Tab Control*/
	--text-color-label: var(--text-color3);
	--text-color-label-hover: var(--text-color2);

	/* Selected element */
	--selected-color: #1A202C;
	--selected-background-color: var(--accent-color3);

	@media (prefers-color-scheme: dark) {
		--main-bg-color: #121212;
		--main-fg-color: #FFFFFF;
		--sidebar-bg-color: #1e1e1e;
		--footer-bg-color: #0c0c0c;
		--footer-fg-color: #c8c8c8;

		--accent-color1: #c32232; 
		--accent-color2: #dd3c4c;
		--accent-color3: #ff6b73;

		--text-color1: #FFFFFF;
		--text-color2: #E0E0E0;
		--text-color3: #c0c0c0;


		--background-color-button: #1e1e1e;

		/* Menu */
		--text-color-menubar: #E0E0E0;
		--text-color-menubar-hover: #b3b3b3;
		--text-color-menubar-selected: var(--accent-color1);
		--text-color-menubar-selected-hover: var(--accent-color2);

		/* Label */
		--text-color-label: var(--text-color3);
		--text-color-label-hover: var(--text-color2);

		/* Selected element */
		--selected-color: #FFFFFF;
		--selected-background-color: var(--accent-color3);
	}
}

* {
	scrollbar-color:var(--footer-bg-color);
}

@media screen and (max-width: 800px) {
	self {
		display:none;
	}
}

@media screen and (max-width: 800px) {
	self {
		width: auto;
	}
}
